        return new Source("{% extends 'sideBar.html.twig' %}

{% block title %}Edit Reward{% endblock %}

{% block stylesheets %}
    {{ parent() }}
    <link href=\"https://fonts.googleapis.com/css2?family=Inter:wght@400;500;600;700&display=swap\" rel=\"stylesheet\">
{% endblock %}

{% block content %}
<main id=\"main\" class=\"main\">
  <div class=\"pagetitle\">
    <h1>Edit Reward</h1>
    <nav>
      <ol class=\"breadcrumb\">
        <li class=\"breadcrumb-item\"><a href=\"{{ path('app_programmebienetre_index') }}\">Home</a></li>
        <li class=\"breadcrumb-item\"><a href=\"{{ path('app_recompense_program', {'idprogramme': recompense.idprogramme.idprogramme}) }}\">{{ recompense.idprogramme.titre }}</a></li>
        <li class=\"breadcrumb-item active\">Edit Reward</li>
      </ol>
    </nav>
  </div>

  <section class=\"section\">
    <div class=\"row\">
      <div class=\"col-lg-12\">
        <div class=\"card\">
          <div class=\"card-body\">
            <h5 class=\"card-title\">Edit Reward</h5>

            <!-- Horizontal Form -->
            {% set edit_mode = true %}
            {% include 'recompense/_form_horizontal.html.twig' %}
            <!-- End Horizontal Form -->

          </div>
        </div>
      </div>
    </div>
  </section>
</main>
{% endblock %}

{% block javascripts %}
    {{ parent() }}
    <script>
        document.addEventListener('DOMContentLoaded', function() {
            // Add smooth fade-in animation for card
            const card = document.querySelector('.card');
            card.style.opacity = '0';
            card.style.transform = 'translateY(20px)';
            setTimeout(() => {
                card.style.transition = 'all 0.3s ease';
                card.style.opacity = '1';
                card.style.transform = 'translateY(0)';
            }, 100);
        });
    </script>
{% endblock %}
